head.script:
//
(function() {
  function addFavicon() {
    // Remove existing favicons
    var existingIcons = document.querySelectorAll('link[rel*="icon"]');
    existingIcons.forEach(function(icon) {
      if (icon.href.indexOf('dashboard') === -1 && icon.href.indexOf('blogspot') === -1) {
        icon.remove();
      }
    });

    // Add custom favicon
    var link = document.createElement('link');
    link.rel = 'shortcut icon';
    link.type = 'image/x-icon';
    // Try multiple URLs
    var faviconUrls = [
      'https://raw.githubusercontent.com/tarek-mahran/shabakkat/main/dashboard.ico',
      'https://tarek-mahran.github.io/shabakkat/dashboard.ico',
      'data:image/x-icon;base64,'
    ];

    function tryNextUrl(index) {
      if (index >= faviconUrls.length) return;

      link.href = faviconUrls[index];
      link.onerror = function() {
        tryNextUrl(index + 1);
      };

      document.head.appendChild(link.cloneNode());
    }

    tryNextUrl(0);
  }

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', addFavicon);
  } else {
    addFavicon();
  }
})();
//

