// SPEED OPTIMIZATION: Preload iframe content
(function() {
  // Create hidden iframe to preload content
  var preloadIframe = document.createElement('iframe');
  preloadIframe.src = 'https://tarek-mahran.github.io/shabakkat/hajj%20dashboard.html';
  preloadIframe.style.display = 'none';
  preloadIframe.style.position = 'absolute';
  preloadIframe.style.left = '-9999px';
  document.addEventListener('DOMContentLoaded', function() {
    document.body.appendChild(preloadIframe);
  });
})();
//

body.script:
//
document.addEventListener('DOMContentLoaded', function(){
  var iframe = document.getElementById('dashboard-iframe');
  var loadingIndicator = document.getElementById('loading-indicator');
  var btn = document.getElementById('fs-btn');
  var container = document.getElementById('pctemp');

  // SPEED OPTIMIZATION: Handle iframe loading states
  if (iframe) {
    // Show loading indicator initially
    iframe.style.opacity = '0';

    // Handle iframe load event
    iframe.addEventListener('load', function() {
      this.classList.add('loaded');
      this.style.opacity = '1';
      if (loadingIndicator) {
        loadingIndicator.style.display = 'none';
      }
    });

    // SPEED OPTIMIZATION: Fallback timeout
    setTimeout(function() {
      if (!iframe.classList.contains('loaded')) {
        iframe.classList.add('loaded');
        iframe.style.opacity = '1';
        if (loadingIndicator) {
          loadingIndicator.style.display = 'none';
        }
      }
    }, 5000); // 5 second fallback
  }

  // SPEED OPTIMIZATION: Optimized fullscreen handler
  if (btn && container) {
    btn.addEventListener('click', function(){
      if (container.requestFullscreen) {
        container.requestFullscreen();
      } else if (container.mozRequestFullScreen) {
        container.mozRequestFullScreen();
      } else if (container.webkitRequestFullscreen) {
        container.webkitRequestFullscreen();
      } else if (container.msRequestFullscreen) {
        container.msRequestFullscreen();
      }
    });
  }

  // SPEED OPTIMIZATION: Intersection Observer for performance
  if ('IntersectionObserver' in window && iframe) {
    var observer = new IntersectionObserver(function(entries) {
      entries.forEach(function(entry) {
        if (entry.isIntersecting) {
          // Iframe is visible, ensure it's fully loaded
          if (!iframe.classList.contains('loaded')) {
            iframe.src = iframe.src; // Refresh if needed
          }
        }
      });
    });
    observer.observe(iframe);
  }
});

// SPEED OPTIMIZATION: Remove Google Ads loading delay
(function() {
  var script = document.createElement('script');
  script.type = 'text/javascript';
  script.async = true;
  script.src = '//pagead2.googlesyndication.com/pagead/js/google_top_exp.js';
  var head = document.getElementsByTagName('head')[0];
  if (head) {
    setTimeout(function() {
      head.appendChild(script);
    }, 2000); // Delay non-critical ads
  }
})();
